window.onload=function(){var e=document.getElementById("video"),n=document.getElementById("play-pause"),t=document.getElementById("mute"),u=document.getElementById("full-screen"),d=document.getElementById("seek-bar"),i=document.getElementById("volume-bar");n.addEventListener("click",function(){e.paused===!0?(e.play(),n.innerHTML="Pause"):(e.pause(),n.innerHTML="Play")}),t.addEventListener("click",function(){e.muted===!1?(e.muted=!0,t.innerHTML="Unmute"):(e.muted=!1,t.innerHTML="Mute")}),u.addEventListener("click",function(){e.requestFullscreen?e.requestFullscreen():e.mozRequestFullscreen?e.mozRequestFullscreen():e.webkitRequestFullscreen&&e.webkitRequestFullscreen()}),d.addEventListener("change",function(){var n=e.duration*(d.value/100);e.currentTime=n}),e.addEventListener("timeupdate",function(){var n=100/e.duration*e.currentTime;d.value=n}),d.addEventListener("mousedown",function(){e.pause()}),d.addEventListener("mouseup",function(){e.play()}),i.addEventListener("change",function(){e.volume=i.value})};